import React, { useState } from 'react';
import { Box, Icon } from '@chakra-ui/react';
import { CheckIcon } from '@chakra-ui/icons';
import './mainPage.css';

const MainPage: React.FC = () => {
  const templates = [
    'SlackBot',
    'FastAPI',
    'CLI tool',
  ];
  const [selectedTemplate, setSelectedTemplate] = useState<string | null>(null);
  const [selectedPath, setSelectedPath] = useState<string | null>(null);

  const handleTemplateClick = (template: string) => {
    setSelectedTemplate(template);
  };

  const handleFileInput = (event: React.ChangeEvent<HTMLInputElement>) => {
    if (event.target.files && event.target.files[0]) {
      setSelectedPath(event.target.files[0].path);
    }
  };

  const handleDuplicate = () => {
    // Logic for duplicating the template in the selected destination
  };

  return (
    <Box className="container">
      <h1 className="title">Select the template you want to duplicate</h1>
      <ul className="list">
        {templates.map((template) => (
          <li
            key={template}
            onClick={() => handleTemplateClick(template)}
            className={`list-item ${selectedTemplate === template ? 'selected' : ''}`}
          >
            {template}
            {selectedTemplate === template && <Icon as={CheckIcon} ml={2} />}
          </li>
        ))}
      </ul>
      <div className="input-wrapper">
        <input
          type="file"
          id="file"
          className="input"
          webkitdirectory="true"
          onChange={handleFileInput}
        />
        <label htmlFor="file" className="input-label">
          <Icon as={CheckIcon} />
          <span className="input-label-text">
            {selectedPath ? 'Path selected' : 'Choose a destination'}
          </span>
        </label>
      </div>
      <button onClick={handleDuplicate} className="button">
        Duplicate
      </button>
    </Box>
  );
};

export default MainPage;
